# --- Derived animation frames (generated from base sprite) --------------------

def _pix_to_grid(pix, w, h):
    return [list(pix[y*w:(y+1)*w]) for y in range(h)]


def _grid_to_pix(grid):
    return [c for row in grid for c in row]


def _shift_region_x(grid, y0, y1, dx, fill=SKY):
    h = len(grid)
    w = len(grid[0])
    y0 = max(0, y0)
    y1 = min(h, y1)
    if dx == 0:
        return
    for y in range(y0, y1):
        row = grid[y]
        new = [fill] * w
        for x, c in enumerate(row):
            if c == fill:
                continue
            nx = x + dx
            if 0 <= nx < w:
                new[nx] = c
        grid[y] = new


def _cap_edges(name: str, *, left: bool = True, right: bool = True, fill=SKY) -> None:
    """Pad unfinished sprite edges.

    The upstream clockface art pack expects some items (clouds/hill/bush) to scroll
    off-screen, so the extreme left/right columns are sometimes left as background.
    On a wrap-around 64x32 matrix this reads as a "cut" edge.

    We do a tiny, conservative cap: if the outermost column is background but the
    adjacent column is not, copy the adjacent column into the outermost.
    """
    a = ASSETS.get(name)
    if not a:
        return
    w = int(a.get('w', 0))
    h = int(a.get('h', 0))
    if w < 3 or h < 1:
        return
    g = _pix_to_grid(a['pix'], w, h)
    for y in range(h):
        if left and g[y][0] == fill and g[y][1] != fill:
            g[y][0] = g[y][1]
        if right and g[y][w - 1] == fill and g[y][w - 2] != fill:
            g[y][w - 1] = g[y][w - 2]
    a['pix'] = _grid_to_pix(g)


def _make_run_frames_from_jump_v2():
    """A cleaner run cycle.

    V1 tried to "wiggle" torso + legs by shifting multiple bands. That made the
    head/body appear to drift separately at low resolution.

    V2 keeps the upper body locked and only animates the lower body. It also
    removes the raised-arm pixels from the jump pose so the run doesn't look like
    a constant jump.
    """
    base = ASSETS['MARIO_JUMP']
    w = int(base['w'])
    h = int(base['h'])
    base_grid = _pix_to_grid(base['pix'], w, h)

    # Remove the "arm up" cluster in the top-right (derived from the jump pose).
    arm_up = {
        (13, 0), (14, 0), (15, 0), (16, 0),
        (13, 1), (14, 1), (15, 1), (16, 1),
        (14, 2), (15, 2), (16, 2),
    }
    g0 = [row[:] for row in base_grid]
    for (x, y) in arm_up:
        if 0 <= y < h and 0 <= x < w:
            g0[y][x] = SKY

    frames = []
    # 4-step cycle: alternate legs/boots. Keep torso + head stable.
    # Lower legs/boots live mostly in rows 12..15 for this sprite.
    for dx in (0, 1, 0, -1):
        g = [row[:] for row in g0]
        _shift_region_x(g, y0=12, y1=h, dx=dx, fill=SKY)
        # Slight knee/hip motion without moving the torso.
        _shift_region_x(g, y0=10, y1=12, dx=(1 if dx == 1 else (-1 if dx == -1 else 0)), fill=SKY)
        frames.append({'w': w, 'h': h, 'pix': _grid_to_pix(g)})
    return frames


def _make_run_frames_from_jump():
    base = ASSETS['MARIO_JUMP']
    w = int(base['w'])
    h = int(base['h'])
    base_grid = _pix_to_grid(base['pix'], w, h)

    frames = []
    # 4-step cycle: shift legs/boots + slight torso counter-wiggle.
    # Reads as "running" on a 64x32 matrix without needing extra source sprites.
    for dx in (0, 1, 0, -1):
        g = [row[:] for row in base_grid]
        _shift_region_x(g, y0=9, y1=h, dx=dx, fill=SKY)
        _shift_region_x(g, y0=6, y1=9, dx=(-dx if dx else 0), fill=SKY)
        frames.append({'w': w, 'h': h, 'pix': _grid_to_pix(g)})
    return frames


# Cap unfinished edges on scrolling items so wrap-around doesn't show raw cuts.
_cap_edges('CLOUD1')
_cap_edges('CLOUD2')
_cap_edges('HILL')
_cap_edges('BUSH')


_run = _make_run_frames_from_jump_v2()
ASSETS['MARIO_RUN0'] = _run[0]
ASSETS['MARIO_RUN1'] = _run[1]
ASSETS['MARIO_RUN2'] = _run[2]
ASSETS['MARIO_RUN3'] = _run[3]
